mapper.select:
SELECT count(*) FROM
	(SELECT * FROM tbl_post




					post_title = #{postTitle}


					AND user_id = (SELECT user_id FROM tbl_user WHERE user_nick_name = #{userNickName})




					post_title LIKE '%${postTitle}%'


					AND user_id = (SELECT user_id FROM tbl_user WHERE user_nick_name LIKE '%${userNickName}%')



		AND important = ${important}
	)
	 NATURAL JOIN
	 (SELECT * FROM tbl_board


			category_id = #{categoryId}


			AND board_id = #{boardId}

	)

WITH tbl_post_board_user
AS (
	SELECT * FROM
		(SELECT * FROM
			(SELECT * FROM tbl_post

				important = ${important}



								AND post_title = #{postTitle}


								AND user_id = (SELECT user_id FROM tbl_user WHERE user_nick_name = #{userNickName})




								AND post_title LIKE '%${postTitle}%'


								AND user_id = (SELECT user_id FROM tbl_user WHERE user_nick_name LIKE '%${userNickName}%')




			 )
			NATURAL JOIN
			(SELECT * FROM tbl_board


					category_id = #{categoryId}


					AND board_id = #{boardId}

			))
		NATURAL JOIN
		tbl_user
	)


			SELECT * FROM tbl_post_board_user
				ORDER BY


						${sortType}


						post_id DESC




			SELECT * FROM
				(SELECT ROWNUM as row_num ,tbl_post_board_user.* FROM
					(SELECT * FROM	 tbl_post_board_user
					ORDER BY


							${sortType}


							post_id DESC


					) tbl_post_board_user)
			WHERE row_num BETWEEN  #{frontPageNum} AND #{rearPageNum}

WITH tbl_post_board_user AS ( SELECT * FROM (SELECT * FROM (SELECT ROWNUM AS row_num,tbl_post.* FROM (SELECT * FROM tbl_post WHERE permission = 1 AND important=0
AND board_id = #{boardId}
ORDER BY post_id DESC) tbl_post ) NATURAL JOIN (SELECT * FROM tbl_board
category_id = #{categoryId}
)) NATURAL JOIN tbl_user )
SELECT * FROM tbl_post_board_user WHERE row_num = (SELECT row_num FROM tbl_post_board_user WHERE post_id = ${postId})+1 OR row_num = (SELECT row_num FROM tbl_post_board_user WHERE post_id = ${postId})-1

mapper.update:
UPDATE tbl_post
post_title = #{postTitle}, post_content = #{postContent}, board_id = #{boardId}, hits = hits+${hits}, comments = comments+${comments}, permission = #{permission}, important = #{important}
WHERE post_id = #{postId}

UPDATE tbl_post

	hits = hits+${hits},
	comments = comments+${comments}

WHERE post_id = #{postId}
