mapper.select:
SELECT * FROM tbl_postfile
WHERE
	user_id = #{userId}
	AND post_id = #{postId}
	AND stored_file_name = #{storedFileName}

SELECT * FROM tbl_postfile
WHERE
	user_id = #{userId}
	AND post_id = #{postId}
	AND file_type = #{fileType}

mapper.delete:
DELETE FROM tbl_postfile
WHERE
	user_id = #{userId}
	AND post_id = #{postId}

	AND stored_file_name = #{storedFileName}
